mapper.select:
select * from user


        id = #{user.id}


        and nick_name like  CONCAT('%',#{user.nickName},'%')


        and sex = #{user.sex}


        and email = #{user.email}


        and birthday >= #{beginTime}


        and birthday <= #{endTime}


        and register_day >= #{beginDay}


        and register_day <= #{endDay}


        and role = #{user.role}
